<?phpuse App\User;use App\Category;use App\SubCategory;use App\Product;use \App\TempCart;use App\CouponCode;if (!function_exists('getAdminDetails')) {    function getAdminDetails() {        $getAdminDetails = User::first();        return $getAdminDetails;    }}if (!function_exists('getCategories')) {    function getCategories() {        $getCategories = Category::pluck('name', 'id');        return $getCategories;    }}if (!function_exists('getAllCategories')) {    function getAllCategories() {        $getAllCategories = Category::get();        return $getAllCategories;    }}if (!function_exists('getAllSubCategories')) {    function getAllSubCategories($catId) {        $getAllSubCategories = SubCategory::where('category_id', $catId)->get();        return $getAllSubCategories;    }}if (!function_exists('getSubCategories')) {    function getSubCategories($cat_id) {        $getSubCategories = SubCategory::where('category_id', $cat_id)->pluck('name', 'id')->prepend('Select Subcategories', '');        return $getSubCategories;    }}//Get Product Detailsif (!function_exists('getProductDetails')) {    function getProductDetails($prod_id) {        $getProductDetails = Product::where('id', $prod_id)->first();        return $getProductDetails;    }}if (!function_exists('cartProdInfo')) {    function cartProdInfo($prod_id, $sess_id) {        $getcartProdDetails = TempCart::where([['product_id', $prod_id], ['session_id', $sess_id]])->first();        return $getcartProdDetails;    }}if (!function_exists('cartDetails')) {    function cartDetails() {        $sess_id = session()->getId();        $cartProdDetails = TempCart::where('session_id', $sess_id)->select(\DB::raw('SUM(quantity) as total_quantity'), \DB::raw('SUM(total_price) as total_price'), \DB::raw('SUM(total_shipping_price) as total_shipping_price'))->first();        return $cartProdDetails;    }}if (!function_exists('singleCartProdInfo')) {    function singleCartProdInfo($cart_id) {        $getSingleCartDetails = TempCart::where('id', $cart_id)->first();        return $getSingleCartDetails;    }}if (!function_exists('disCountCal')) {    function disCountCal($discountVal) {        $cartDetails = cartDetails();        $discountAmount = number_format($cartDetails->total_price * $discountVal / 100, 2);        $afterDiscountAmt = number_format(($cartDetails->total_price) - $discountAmount, 2);        if ($cartDetails->total_price >= 50) {            $totalPayble = number_format($afterDiscountAmt + 0.00, 2);        } else {            $totalPayble = number_format($afterDiscountAmt + $cartDetails->total_shipping_price, 2);        }        return array('discountAmount' => $discountAmount, 'afterDiscountAmt' => $afterDiscountAmt, 'totalPayble' => $totalPayble, 'discount_percentage' => $discountVal);    }}if (!function_exists('allProductDetails')) {    function allProductDetails() {        $allProductDetails = Product::select(\DB::raw('min(price) as min_price'), \DB::raw('max(price) as max_price'))->first();        return $allProductDetails;    }}#############  Booking Part Helper #########################if (!function_exists('getTimeFrame15Min')) {    function getTimeFrame15Min() {        $start = strtotime('10:00');        $end = strtotime('17:00');        $timeArr = [];        for ($i = $start; $i < $end; $i = $i + 15 * 60) {            $timeArr[] = date('H:i', $i);        }        //dd($timeArr);        return $timeArr;    }}